commits.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

/* Definition of the design system. All colors, gradients, fonts, etc should be defined here. 
All colors MUST be HSL.
*/

@layer base {
  :root {
    --background: 60 100% 97%;
    --foreground: 0 0% 8%;

    --card: 60 100% 99%;
    --card-foreground: 0 0% 8%;

    --popover: 60 100% 99%;
    --popover-foreground: 0 0% 8%;

    --primary: 0 0% 8%;
    --primary-foreground: 60 100% 95%;

    --secondary: 60 100% 85%;
    --secondary-foreground: 0 0% 15%;

    --muted: 60 30% 92%;
    --muted-foreground: 0 0% 45%;

    --accent: 60 100% 50%;
    --accent-foreground: 0 0% 8%;

    --yellow-bright: 60 100% 50%;
    --yellow-light: 60 100% 85%;
    --yellow-glow: 60 100% 70%;
    --black-primary: 0 0% 8%;
    --black-secondary: 0 0% 15%;

    --gradient-primary: linear-gradient(135deg, hsl(var(--yellow-bright)), hsl(var(--yellow-glow)));
    --gradient-hero: linear-gradient(135deg, hsl(var(--black-primary)), hsl(var(--black-secondary)));
    --shadow-glow: 0 0 30px hsl(var(--yellow-bright) / 0.3);
    --shadow-card: 0 10px 30px -10px hsl(var(--black-primary) / 0.1);

    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;

    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 222.2 84% 4.9%;

    --radius: 0.5rem;

    --sidebar-background: 0 0% 98%;

    --sidebar-foreground: 240 5.3% 26.1%;

    --sidebar-primary: 240 5.9% 10%;

    --sidebar-primary-foreground: 0 0% 98%;

    --sidebar-accent: 240 4.8% 95.9%;

    --sidebar-accent-foreground: 240 5.9% 10%;

    --sidebar-border: 220 13% 91%;

    --sidebar-ring: 217.2 91.2% 59.8%;
  }

  .dark {
    --background: 0 0% 5%;
    --foreground: 60 100% 95%;

    --card: 0 0% 8%;
    --card-foreground: 60 100% 95%;

    --popover: 0 0% 8%;
    --popover-foreground: 60 100% 95%;

    --primary: 60 100% 50%;
    --primary-foreground: 0 0% 8%;

    --secondary: 0 0% 15%;
    --secondary-foreground: 60 100% 95%;

    --muted: 0 0% 12%;
    --muted-foreground: 0 0% 65%;

    --accent: 60 100% 50%;
    --accent-foreground: 0 0% 8%;

    --yellow-bright: 60 100% 50%;
    --yellow-light: 60 100% 85%;
    --yellow-glow: 60 100% 70%;
    --black-primary: 0 0% 8%;
    --black-secondary: 0 0% 15%;

    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;

    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 212.7 26.8% 83.9%;
    --sidebar-background: 240 5.9% 10%;
    --sidebar-foreground: 240 4.8% 95.9%;
    --sidebar-primary: 224.3 76.3% 48%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 240 3.7% 15.9%;
    --sidebar-accent-foreground: 240 4.8% 95.9%;
    --sidebar-border: 240 3.7% 15.9%;
    --sidebar-ring: 217.2 91.2% 59.8%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
  }
}

/* Enhanced animations for better user experience */
@keyframes fade-in {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slide-up {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes scale-in {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes bounce-in {
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes glow {
  0%, 100% {
    box-shadow: 0 0 5px hsl(var(--accent) / 0.5);
  }
  50% {
    box-shadow: 0 0 20px hsl(var(--accent) / 0.8), 0 0 30px hsl(var(--accent) / 0.6);
  }
}

.animate-fade-in {
  animation: fade-in 0.6s ease-out forwards;
}

.animate-slide-up {
  animation: slide-up 0.5s ease-out forwards;
}

.animate-scale-in {
  animation: scale-in 0.4s ease-out forwards;
}

.animate-bounce-in {
  animation: bounce-in 0.6s ease-out forwards;
}

.animate-glow {
  animation: glow 2s ease-in-out infinite;
}

.hover-scale {
  transition: transform 0.2s ease-in-out;
}

.hover-scale:hover {
  transform: scale(1.05);
}
